package com.example.root.sqliteproject;import android.view.View;import android.widget.AdapterView;public class CustomClickListener implements View.OnClickListener{    private int position;    private OnItemClickCallback onItemClickCallback;    public CustomClickListener(int position, OnItemClickCallback onItemClickCallback) {        this.position = position;        this.onItemClickCallback = onItemClickCallback;    }    @Override    public void onClick(View v) {        onItemClickCallback.onItemClicked(v, position);    }    public interface OnItemClickCallback{        void onItemClicked(View view, int position);    }}