package com.example.root.sqliteproject;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;public class DatabaseHelper extends SQLiteOpenHelper{    public static String DB_NAME    = "dbnote";    private static int DB_VERSION   = 1;    private static final String CREATE_NOTE_TABLE = String.format(            "CREATE TABLE %s"            +" ( %s INTEGER PRIMARY KEY AUTOINCREMENT, "            +" %s TEXT NOT NULL,"            +" %s TEXT NOT NULL,"            +" %s TEXT NOT NULL)",            DatabaseContract.TABLE_NOTE,            DatabaseContract.NoteColumns._ID,            DatabaseContract.NoteColumns.TITLE,            DatabaseContract.NoteColumns.DESCRIPTION,            DatabaseContract.NoteColumns.DATE    );    public DatabaseHelper(Context context) {        super(context, DB_NAME, null, DB_VERSION);    }    @Override    public void onCreate(SQLiteDatabase db) {        db.execSQL(CREATE_NOTE_TABLE);    }    @Override    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {        db.execSQL("DROP TABLE IF EXISTS "+DatabaseContract.TABLE_NOTE);        onCreate(db);    }}