package com.example.root.sqliteproject;import android.app.Activity;import android.content.Intent;import android.support.annotation.NonNull;import android.support.v7.widget.CardView;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.LinkedList;public class NoteAdapter extends RecyclerView.Adapter<NoteAdapter.NoteViewHolder>{    private LinkedList<Note> linkedList;    private Activity activity;    public NoteAdapter(Activity activity) {        this.activity = activity;    }    public LinkedList<Note> getLinkedList() {        return linkedList;    }    public void setLinkedList(LinkedList<Note> linkedList) {        this.linkedList = linkedList;    }    @NonNull    @Override    public NoteAdapter.NoteViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_note, parent, false);        return new NoteViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull NoteAdapter.NoteViewHolder holder, int position) {        holder.tvTitle.setText(getLinkedList().get(position).getTitle());        holder.tvDesc.setText(getLinkedList().get(position).getDescription());        holder.tvDate.setText(getLinkedList().get(position).getDate());        holder.note.setOnClickListener(new CustomClickListener(position, new CustomClickListener.OnItemClickCallback() {            @Override            public void onItemClicked(View view, int position) {                Intent intent = new Intent(activity, FormAddUpdateActivity.class);                intent.putExtra(FormAddUpdateActivity.EXTRA_POSITION, position);                intent.putExtra(FormAddUpdateActivity.EXTRA_NOTE, getLinkedList().get(position));                activity.startActivityForResult(intent, FormAddUpdateActivity.REQUEST_UPDATE);            }        }));    }    @Override    public int getItemCount() {        return getLinkedList().size();    }    public class NoteViewHolder extends RecyclerView.ViewHolder {        TextView tvTitle, tvDesc, tvDate;        CardView note;        public NoteViewHolder(View itemView) {            super(itemView);            tvTitle = (TextView)itemView.findViewById(R.id.tv_title);            tvDesc  = (TextView)itemView.findViewById(R.id.tv_desc);            tvDate  = (TextView)itemView.findViewById(R.id.tv_date);            note    = (CardView)itemView.findViewById(R.id.cv_item);        }    }}